// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.14;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "../../interfaces/ISolver.sol";

import "../../solvers/SolverLib.sol";

import "../Module.sol";

contract Unanimity is Module {
    constructor() {
        permits.push(ISolver.setTimelock.selector);
    }

    function load(bytes calldata data) external override {
        ISolver(msg.sender).setState(
            STATEKEY,
            abi.encode(getNumUniqueRecipients(ISolver(msg.sender)))
        );
        emit LoadedModule(address(this), msg.sender);
    }

    function approveOutcome(ISolver solver, uint256 conditionIndex)
        external
        returns (address[] memory approved)
    {
        require(
            solver.isRecipient(msg.sender, conditionIndex),
            "Only recipients"
        );
        require(
            solver.getStatus(conditionIndex) ==
                SolverLib.Status.OutcomeProposed,
            "Not proposes"
        );

        uint256 numRecipients = abi.decode(
            solver.getState(STATEKEY),
            (uint256)
        );

        bytes32 APPROVERS_STATEKEY = keccak256(
            abi.encode(STATEKEY, "APPROVERS_STATEKEY", conditionIndex)
        );

        address[] memory approvers;

        bytes memory approverData = solver.getState(APPROVERS_STATEKEY);

        if (approverData.length > 0) {
            approvers = abi.decode(approverData, (address[]));
        }

        for (uint256 i = 0; i < approvers.length; i++) {
            if (msg.sender == approvers[i]) {
                revert("Already approved");
            }
        }

        if (approvers.length + 1 == numRecipients) {
            solver.setTimelock(conditionIndex, 0);
            solver.confirmPayouts(conditionIndex);
        } else {
            address[] memory newApprovers = new address[](approvers.length + 1);
            for (uint256 i = 0; i < approvers.length; i++) {
                newApprovers[i] = approvers[i];
            }
            newApprovers[approvers.length] = msg.sender;
            solver.setState(APPROVERS_STATEKEY, abi.encode(newApprovers));
        }

        return approvers;
    }

    function getNumUniqueRecipients(ISolver solver)
        public
        view
        returns (uint256)
    {
        SolverLib.Config memory config = solver.getConfig();

        bytes32[] memory recipientSlots = new bytes32[](
            config.conditionBase.allocations.length
        );

        uint256 numDuplicates = 0;

        for (uint256 i = 0; i < config.conditionBase.allocations.length; i++) {
            bool duplicate = false;

            for (uint256 j = 0; j < recipientSlots.length; j++) {
                if (
                    recipientSlots[j] ==
                    config.conditionBase.allocations[i].recipientAddressSlot
                ) {
                    duplicate = true;
                }
            }

            if (!duplicate) {
                recipientSlots[i - numDuplicates] = (
                    config.conditionBase.allocations[i].recipientAddressSlot
                );
            } else {
                numDuplicates++;
            }
        }

        return recipientSlots.length - numDuplicates;
    }
}

contracts/modules/keepers/Keeper.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.14;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "../../interfaces/IConditionalTokens.sol";
import "../../interfaces/ISolver.sol";

import "../../interfaces/ISolver.sol";
import "../../solvers/SolverLib.sol";

import "../Module.sol";

abstract contract Keeper is Module {
    function prepareSolve(address solver, uint256 conditionIndex)
        public
        virtual
    {
        ISolver(solver).prepareSolve(conditionIndex);
    }

    function deployChild(address solver, SolverLib.Config calldata config)
        public
        virtual
    {
        ISolver(solver).deployChild(config);
    }

    function executeSolve(address solver, uint256 conditionIndex)
        public
        virtual
    {
        ISolver(solver).executeSolve(conditionIndex);
    }

    function addData(
        address solver,
        bytes32 slot,
        bytes memory data
    ) public virtual {
        ISolver(solver).addData(slot, data);
    }

    function proposePayouts(
        address solver,
        uint256 index,
        uint256[] calldata payouts
    ) public virtual {
        ISolver(solver).proposePayouts(index, payouts);
    }

    function confirmPayouts(address solver, uint256 index) public virtual {
        ISolver(solver).confirmPayouts(index);
    }

    function redeemPosition(
        address solver,
        IERC20 collateralToken,
        bytes32 parentCollectionId,
        bytes32 conditionId,
        uint256[] calldata indexSets
    ) public virtual {
        ISolver(solver).redeemPosition(
            collateralToken,
            parentCollectionId,
            conditionId,
            indexSets
        );
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override
        returns (bool)
    {
        return
            interfaceId == type(Keeper).interfaceId ||
            super.supportsInterface(interfaceId);
    }
}

contracts/modules/IPFSTextSubmitter.sol.content:
// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.8.14;

import "../solvers/SolverLib.sol";
import "../interfaces/ISolver.sol";
import "./Module.sol";

contract IPFSTextSubmitter is Module {
    event SubmittedWork(
        address indexed solver,
        string cid,
        address submitter,
        bytes32 indexed conditionId
    );

    function load(bytes memory data) external override {
        ISolver(msg.sender).setState(STATEKEY, data);

        emit LoadedModule(address(this), msg.sender);
    }

    function submitter(ISolver solver) public view returns (address) {
        return
            abi.decode(
                solver.getData(bytes32(solver.getState(STATEKEY))),
                (address)
            );
    }

    function submit(
        ISolver solver,
        string calldata cid,
        uint256 conditionIndex
    ) external {
        require(msg.sender == submitter(solver), "Only Submitter");
        SolverLib.Condition memory condition = solver.condition(conditionIndex);

        require(condition.status == SolverLib.Status.Executed, "Disabled");
        emit SubmittedWork(
            address(solver),
            cid,
            msg.sender,
            condition.conditionId
        );
    }
}

contracts/solvers/ERC1155Rescue.sol.content:
// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.14;

import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Receiver.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../conditionalTokens/ConditionalTokens.sol";

contract ERC1155Rescue is ERC1155Receiver {
    using SafeERC20 for IERC20;

    ConditionalTokens ctf;

    struct Bag {
        bool rescued;
        address recipient;
        bytes32 id;
        bytes32 trackingId;
        uint256[] tokens;
        uint256[] amounts;
    }

    mapping(bytes32 => Bag) bags;

    event ReceivedBag(
        bytes32 indexed id,
        address indexed recipient,
        uint256[] tokenIds,
        uint256[] amounts
    );

    event RescuedBag(
        bytes32 indexed id,
        address indexed to,
        uint256[] tokenIds,
        uint256[] amounts
    );

    constructor(ConditionalTokens _ctf) {
        ctf = _ctf;
    }

    function rescueBag(bytes32 bagId, address to) external {
        Bag storage bag = bags[bagId];
        require(
            msg.sender == bag.recipient,
            "ERC1155Rescue::Only OG recipient"
        );
        require(bag.id == bagId, "ERC1155Rescue::Invalid bagId");
        require(bag.rescued == false, "ERC1155Rescue::Rescued");

        bag.rescued = true;

        ctf.safeBatchTransferFrom(
            address(this),
            to,
            bag.tokens,
            bag.amounts,
            abi.encode(bag.trackingId)
        );

        emit RescuedBag(bagId, to, bag.tokens, bag.amounts);
    }

    function getBagId(
        address solver,
        uint256[] calldata tokenIds,
        uint256[] calldata tokenAmounts
    ) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(solver, tokenIds, tokenAmounts));
    }

    function onERC1155BatchReceived(
        address operator,
        address from,
        uint256[] calldata ids,
        uint256[] calldata values,
        bytes calldata data
    ) external virtual override returns (bytes4) {
        // Should only receive batch transfers from solver during `executeSolve`
        require(operator == from, "ERC1155Rescue::operator != from");
        bytes32 bagId = getBagId(from, ids, values);

        Bag storage bag = bags[bagId];
        require(bag.id != bagId, "ERC1155Rescue::Bag already exists");

        bag.id = bagId;
        bag.tokens = ids;
        bag.amounts = values;

        (bag.recipient, bag.trackingId) = abi.decode(data, (address, bytes32));

        emit ReceivedBag(bagId, bag.recipient, ids, values);

        return this.onERC1155BatchReceived.selector;
    }

    function onERC1155Received(
        address operator,
        address from,
        uint256 id,
        uint256 value,
        bytes calldata data
    ) external virtual override returns (bytes4) {
        operator;
        from;
        id;
        value;
        data;
        revert();
    }
}

